'use strict';

export default function(app) {
	app.config(config);

	function config($logProvider, $compileProvider, $mdAriaProvider, $mdThemingProvider, $sceDelegateProvider, $locationProvider, $qProvider) {
		'ngInject';

		$sceDelegateProvider.resourceUrlWhitelist([
			// Allow same origin resource loads.
			'self',
			// Allow loading from our assets domain. **.
			'cdnUrl'
		]);

		// to handle Possibly unhandled rejection: {} erroe which is generated by ui-router in angular 1.6.1 version.
		$qProvider.errorOnUnhandledRejections(false);

		// Globally disables all ARIA warnings.
		$mdAriaProvider.disableWarnings();

		//require for md-datepicker to display with angular 1.6 v
		$compileProvider.preAssignBindingsEnabled(true);

		//following angular's recommendation when going to production
		//https://docs.angularjs.org/guide/production
		if (process.env.NODE_ENV === 'production') {
			$logProvider.debugEnabled(false);
			$compileProvider.debugInfoEnabled(false);
		}

		$locationProvider.hashPrefix('!');

		$mdThemingProvider.theme('default')
			.primaryPalette('cyan', {
				'default': '800',
			})
			.accentPalette('red', {
				'default': 'A200',
			});
	}
}